import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import queryString from "query-string";

const root = ReactDOM.createRoot(document.getElementById('root'));
const parsed = queryString.parse(window.location.search);

const server = "http://localhost"
const port = "5067"
const host = `${server}:${port}`
//const host = window.env.REACT_APP_API_BASE_URL

const program_no = window.env.REACT_APP_PROGRAM_NO;

parsed.program_no = program_no;

fetch(`${host}/api/pub/check`, {
    method: "POST",
    headers: {
        'content-type': 'application/json'
    },
    body: JSON.stringify(parsed)
}).then(function (res) {
    return res.json();
}).then(function (data) {
    if (data.ident) {
        root.render(
            <React.StrictMode>
                <App user_id={data.msg} program_no={program_no} host={host}/>
            </React.StrictMode>
        );
    } else {
        alert(data.msg)
    }
}).catch(function (Error) {
    console.log(Error)
})
