import React, {useState, useEffect} from "react";
import './App.css';

async function postData(json, api) {
    const res = await fetch(api, {
        method: "POST",
        headers: {
            'content-type': 'application/json'
        },
        body: JSON.stringify(json)
    })
    return res;
}

const App = ({user_id, program_no, host}) => {
    function sendAPI() {
        var FileName;

        postData({Id: user_id}, `${host}/api/pub/acad`).then(function (response) {
            return response.json()
        }).then(function (data) {
            postData({Acadno: data.id}, `${host}/api/pub/date`)
                .then(function (response) {
                    if(!response)
                        throw new Error("不在時限內");
                    return response.json()
                }).then(function (data) {
                if (data) {
                    var json = {}
                    json.syearEnd = data.syearEnd;
                    json.semEnd = data.semEnd;
                    json.StudentId = user_id;
                    postData(json, `${host}/api/Calculate`)
                        .then(function (response) {
                            return postData(json, `${host}/api/generate/inform`);
                        }).then(function (response) {
                        var FileNameEncode = response.headers.get('Content-Disposition').split('filename=')[1];
                        FileName = decodeURIComponent(FileNameEncode);
                        return response.blob();
                    }).then(function (blob) {
                        var url = window.URL.createObjectURL(blob);
                        var a = document.createElement('a');
                        a.href = url;
                        a.download = FileName;
                        document.body.appendChild(a);
                        a.click();
                        window.URL.revokeObjectURL(url);
                        document.body.removeChild(a);
                    }).catch(function (Error) {
                        console.log(Error);
                    });
                } else alert("不在時限內");
            }).catch(function (Error) {
                console.log(Error);
            });
        }).catch(function (Error) {
            console.log(Error);
        });

    }

    return (
        <div className="App">
            <div>
                <button className="submit" onClick={sendAPI}>送出</button>
            </div>
        </div>
    );
}

export default App;
