import {useState, useEffect} from "react";
import './App.css';
import Datalist from "./components/datalist";
import Select from "./components/select";

async function getData(setData) {
    const res = await fetch("http://localhost:55082/api/get")
    const data = await res.json()
    setData(data);
}

async function postData(Data, setData) {
    var json = {}
    json.Id = Data
    const res = await fetch("http://localhost:55082/api/get", {
        method: "POST",
        headers: {
            'content-type': 'application/json'
        },
        body: JSON.stringify(json)
    })
    const data = await res.json()
    const defaultData = [{
        "Id": "0",
        "Name": "不分組"
    }];
    if(data.length === 0)
        Object.assign(data, defaultData);

    console.log(data)
    setData(data)
}

const App = () => {
    var chineseNumber = ("一二三四五六七八九").split('');
    var chineseClass = ("甲乙丙丁戊己庚辛壬癸子丑寅卯").split('');
    const [DeptData, setDeptData] = useState([]);
    const [SecData, setSecData] = useState([]);
    const [GradeData] = useState(() => {
        var json = []
        for(var i = 0; i < 4; i++){
            json.push({"Id":i + 1,"Name":chineseNumber[i]})
        }
        return json
    });
    const [ClassData] = useState(() => {
        var json = []
        for(var i = 0; i < 4; i++){
            json.push({"Id":i + 1,"Name":chineseClass[i]})
        }
        return json
    });
    const [SyearData] = useState(() => {
        const currentYear = new Date().getFullYear() - 1911;
        const years = Array.from({ length: 10 }, (_, index) => currentYear - index);
        var json = []
        for(const year of years){
            json.push({"Id":year,"Name":year})
        }
        return json
    });
    const [SemData] = useState(() => {
        var json = []
        for(var i = 1; i <= 3; i++){
            json.push({"Id":i,"Name":i})
        }
        return json
    });
    const [option, setOption] = useState("");

    useEffect(() => {
        getData(setDeptData)
    }, [])

    useEffect(() => {
        postData(option, setSecData)
    }, [option])

    return (
        <div className="App">
            <div className="dept">
                <p>系所：</p>
                <Datalist Id="dept_list" SelectData={DeptData} setOption={setOption}/>
            </div>
            <div className="sec">
                <p>組別：</p>
                <Select SelectData={SecData}/>
            </div>
            <div className="grade">
                <p>年級：</p>
                <Select SelectData={GradeData}/>
            </div>
            <div className="class">
                <p>班級：</p>
                <Select SelectData={ClassData}/>
            </div>
            <div className="stu">
                <p>學號：</p>

            </div>
            <div className="syear">
                <p>學年：</p>
                <Select SelectData={SyearData}/>
            </div>
            <div className="sem">
                <p>學期：</p>
                <Select SelectData={SemData}/>
            </div>
        </div>
    );
}

export default App;
