import {useState, useEffect} from "react";
import './App.css';
import Select from "./components/select";

async function getData(setData) {
    const res = await fetch("http://localhost:55082/api/get")
    const data = await res.json()
    setData(data);
}

async function postData(Data, setData) {
    var json = {}
    json.Id = Data
    const res = await fetch("http://localhost:55082/api/get", {
        method: "POST",
        headers: {
            'content-type': 'application/json'
        },
        body: JSON.stringify(json)
    })
    const data = await res.json()
    const defaultData = [{
        "Id": "0",
        "Name": "不分組"
    }];
    if(data.length === 0)
        Object.assign(data, defaultData);

    setData(data)
}

const App = () => {
    const [DeptData, setDeptData] = useState([]);
    const [SecData, setSecData] = useState([]);
    const [GradeData] = useState([{
        "Id": "1",
        "Name": "一"
    },{
        "Id": "2",
        "Name": "二"
    },{
        "Id": "3",
        "Name": "三"
    },{
        "Id": "4",
        "Name": "四"
    }]);
    const [ClassData] = useState([{
        "Id": "1",
        "Name": "甲"
    }]);
    const [SyearData,setSyearData] = useState([]);
    const [option, setOption] = useState("");

    useEffect(() => {
        getData(setDeptData)
    }, [])

    useEffect(() => {
        postData(option, setSecData)
    }, [option])

    function year(){
        const currentYear = new Date().getFullYear() - 1911;
        const years = Array.from({ length: 10 }, (_, index) => currentYear - index);
        var json = [{}]
        for(const year of years){
            json.push({"Id":{year},"Name":{year}})
        }
        setSyearData(json)
    }

    return (
        <div className="App">
            <div className="dept">
                <p>系所：</p>
                <Select SelectData={DeptData} setOption={setOption}/>
            </div>
            <div className="sec">
                <p>組別：</p>
                <Select SelectData={SecData}/>
            </div>
            <div className="grade">
                <p>年級：</p>
                <Select SelectData={GradeData}/>
            </div>
            <div className="class">
                <p>班級：</p>
                <Select SelectData={ClassData}/>
            </div>
            <div className="syear">
                {year()}
                <p>學年：</p>
                <Select SelectData={SyearData}/>
            </div>
        </div>
    );
}

export default App;
