import Item from "./item";

const datalist = ({htmlId, Invalid, SelectData, setOption = () => {}}) => {
    function optionChange(e){
        setOption(e.target.value)
    }

    return (<div className="datalist">
        <input list={htmlId} onChange={optionChange} className={"form-control datalist_input".concat(Invalid ? " is-invalid" : "")}/>
        <datalist id={htmlId}>
            {SelectData.map((item) => {
                const {id, name} = item;
                return (
                    <Item
                        Id={id}
                        Name={name}
                    />
                );
            })
            }
        </datalist>
    </div>)
}

export default datalist
