import Item from "./item";
import {useEffect} from "react";
const select = ({htmlId, SelectData, setOption = () => {}}) => {
    function optionChange(e){
        setOption(e.target.value)
    }

    // eslint-disable-next-line react-hooks/rules-of-hooks
    useEffect(() => {
        const initialSelectedValue = SelectData.length > 0 ? SelectData[0].id : "";
        setOption(initialSelectedValue);
    }, [SelectData, setOption]);

    return (<div className="select">
        <select id={htmlId} className="form-select" onChange={optionChange}>
            {SelectData.map((item) => {
                const {id, name} = item;
                return (
                    <Item Id={id} Name={name} />
                );
            })
            }
        </select>
    </div>)
}

export default select
