import {useState, useEffect} from "react";
import './App.css';
import Select from "./components/select";

async function getData(setData) {
    const res = await fetch("http://localhost:55082/api/get")
    const data = await res.json()
    setData(data);
}

async function postData(Data, setData) {
    const res = await fetch("http://localhost:55082/api/get", {
        method: "POST",
        headers: {
            'content-type': 'application/json'
        },
        body: JSON.stringify(Data)
    })
    const data = await res.json()
    setData(data)
}

const App = () => {
    const [DeptData, setDeptData] = useState([]);
    const [SecData, setSecData] = useState([]);

    const [option, setOption] = useState("");

    useEffect(() => {
        getData(setDeptData)
    }, [])

    useEffect(() => {
        postData(option)
        console.log(option)
    }, [option])

    return (
        <div className="App">
            <div className="dept">
                <p>系所：</p>
                <Select SelectData={DeptData} setOption={setOption}/>
            </div>
            <div className="sec">
                <p>組別：</p>
                <Select SelectData={SecData}/>
            </div>
        </div>
    );
}

export default App;
