import React, {useState, useEffect} from "react";
import './App.css';
import Datalist from "./components/datalist";
import Select from "./components/select";

async function postData(json, api) {
    const res = await fetch(api, {
        method: "POST",
        headers: {
            'content-type': 'application/json'
        },
        body: JSON.stringify(json)
    })
    return res;
}

const App = ({user_id, program_no, host}) => {
    var chineseNumber = ("一二三四五六七八九").split('');
    var chineseClass = ("甲乙丙丁戊己庚辛壬癸子丑寅卯").split('');
    const [DeptData, setDeptData] = useState([]);
    const [SecData, setSecData] = useState([]);
    const [GradeData] = useState(() => {
        var json = []
        for (var i = 0; i < 5; i++) {
            json.push({"id": i + 1, "name": chineseNumber[i]})
        }
        return json
    });

    const [ClassData] = useState(() => {
        var json = []
        for (var i = 0; i < 14; i++) {
            json.push({"id": i + 1, "name": chineseClass[i]})
        }
        return json
    });

    const [SyearData] = useState(() => {
        const currentYear = new Date().getFullYear() - 1911;
        const years = Array.from({length: 20}, (_, index) => currentYear - index);
        var json = []
        for (const year of years) {
            json.push({"id": year, "name": year})
        }
        return json
    });

    const [SemData] = useState(() => {
        var json = []
        for (var i = 1; i <= 3; i++) {
            json.push({"id": i, "name": i})
        }
        return json
    });

    const [StuData, setStuData] = useState([]);
    const [deptOption, setDeptOption] = useState("");
    const [secOption, setSecOption] = useState("");
    const [gradeOption, setGradeOption] = useState("");
    const [classOption, setClassOption] = useState("");
    const [syearOption, setSyearOption] = useState("");
    const [semOption, setSemOption] = useState("");
    const [stuOption, setStuOption] = useState("");
    const [isRank, setIsRank] = useState(false);
    const [isGrading, setIsGrading] = useState(false);
    const [isLoading, setIsLoading] = useState(false);

    function rank(e) {
        setIsRank(e.target.checked);
    }

    function grading(e) {
        setIsGrading(e.target.checked);
    }

    function sendAPI(e) {
        setIsLoading(true)
        var json = {}
        var range = {};
        json.user_id = user_id;
        json.DeptId = deptOption;
        json.Secno = secOption;
        json.Grade = gradeOption;
        json.Clacod = classOption;
        json.syearEnd = syearOption;
        json.semEnd = semOption;
        json.Isrank = isRank;
        json.StudentId = stuOption;
        json.IsGrading = isGrading;

        range.user_id = user_id;
        range.program_no = program_no;
        range.data = json.DeptId;
        range.tblname = "STUFILE";
        range.clnname = "DEPTNO";
        if (deptOption === "")
            alert("需輸入系所")

        if (stuOption !== "") {
            json.DeptId = null;
            json.Secno = 0;
            json.Grade = 0;
            json.Clacod = 0;
        } else
            json.StudentId = null;

        var FileName;
        postData(range, `${host}/api/pub/range`)
            .then(function (response) {
                return response.json()
            }).then(function (data) {
            if (data) {
                postData(json, `${host}/api/Calculate`)
                    .then(function (response) {
                        return postData(json, `${host}/api/generate/eng`);
                    }).then(function (response) {
                    var FileNameEncode = response.headers.get('Content-Disposition').split('filename=')[1];
                    FileName = decodeURIComponent(FileNameEncode);
                    return response.blob();
                }).then(function (blob) {
                    var url = window.URL.createObjectURL(blob);
                    var a = document.createElement('a');
                    a.href = url;
                    a.download = FileName;
                    document.body.appendChild(a);
                    a.click();
                    window.URL.revokeObjectURL(url);
                    document.body.removeChild(a);
                    setIsLoading(false);
                }).catch(function (Error) {
                    console.log(Error);
                    setIsLoading(false);
                });
            } else {
                alert("無權存取資料");
                setIsLoading(false);
            }

        }).catch(function (Error) {
            console.log(Error);
            setIsLoading(false);
        });
    }

    useEffect(() => {
        var range = {}
        range.user_id = user_id;
        range.program_no = program_no;
        range.tblname = "STUFILE";
        range.clnname = "DEPTNO";
        postData(range, `${host}/api/pub/dept`)
            .then(function (res) {
                return res.json();
            }).then(function (data) {
            setDeptData(data);
        }).catch(function (Error) {
            console.log(Error)
        })
    }, [])

    useEffect(() => {
        var json = {}
        json.Id = deptOption
        postData(json, `${host}/api/pub/sec`)
            .then(function (res) {
                return res.json();
            }).then(function (data) {
            const defaultData = [{
                "id": "0",
                "name": "不分組"
            }];
            if (data.length === 0)
                Object.assign(data, defaultData);

            setSecData(data)
        }).catch(function (Error) {
            console.log(Error)
        })
    }, [deptOption])

    useEffect(() => {
        var json = {}
        json.DeptId = deptOption;
        json.Secno = secOption;
        json.Grade = gradeOption;
        json.Clacod = classOption;
        json.syearEnd = syearOption;
        json.semEnd = semOption;
        if (deptOption !== "" && secOption !== "" && gradeOption !== "" && classOption !== "" && syearOption !== "" && semOption !== "")
            postData(json, `${host}/api/pub/stu`)
                .then(function (res) {
                    return res.json();
                }).then(function (data) {
                setStuData(data)
            }).catch(function (Error) {
                console.log(Error)
            })
    }, [deptOption, secOption, gradeOption, classOption, syearOption, semOption])

    return (
        <div className="App">
            <div className="container">
                <div className="img">
                    <img alt="title" src="https://wwwcms.ncyu.edu.tw/images/logo.png"/>
                </div>

                <div className="row">
                    <div className="col">
                        <label htmlFor="dept_list" className="form-label">系所</label>
                        <Datalist htmlId="dept_list" SelectData={DeptData} setOption={setDeptOption}/>
                    </div>
                </div>

                <div className="row">
                    <div className="col  ">
                        <div className="row my-3">
                            <label htmlFor="sec_list" className="form-label">組別</label>
                            <Select htmlId="sec_list" SelectData={SecData} setOption={setSecOption}/>
                        </div>
                        <div className="row my-3">
                            <label htmlFor="grade_list" className="form-label">年級</label>
                            <Select htmlId="grade_list" SelectData={GradeData} setOption={setGradeOption}/>
                        </div>
                        <div className="row my-3">
                            <label htmlFor="class_list" className="form-label">班級</label>
                            <Select htmlId="class_list" SelectData={ClassData} setOption={setClassOption}/>
                        </div>
                    </div>

                    <div className="col">
                        <label htmlFor="syear_list" className="form-label">學年</label>
                        <Select htmlId="syear_list" SelectData={SyearData} setOption={setSyearOption}/>

                        <label htmlFor="sem_list" className="form-label">學期</label>
                        <Select htmlId="sem_list" SelectData={SemData} setOption={setSemOption}/>

                        <label htmlFor="stu_list" className="form-label">學號</label>
                        <Datalist htmlId="stu_list" SelectData={StuData} setOption={setStuOption}/>
                    </div>
                </div>

                <div className="row">
                    <div className="col">
                        <div className="form-check">
                            <input className="form-check-input" type="checkbox" onChange={rank} id="rank"/>
                            <label className="form-check-label" htmlFor="rank">
                                顯示排名
                            </label>
                        </div>
                    </div>
                </div>

                <div className="row">
                    <div className="col">
                        <div className="form-check">
                            <input className="form-check-input" type="checkbox" onChange={grading} id="grading"/>
                            <label className="form-check-label" htmlFor="grading">
                                用等第顯示
                            </label>
                        </div>
                    </div>
                </div>

                <button className="btn btn-primary" onClick={sendAPI} disabled={isLoading}>送出</button>
            </div>
        </div>
    );
}

export default App;
